import React, { PureComponent } from 'react';
import PropTypes from 'prop-types';
import { devicePropType } from '../constants';

export default class GroupForm extends PureComponent {
    handleCancelClick = () => {
        window.history.back();
    };

    handleSubmit = (event) => {
        this.props.onSubmit({
            ...this.props.device,
            name: event.target.deviceName.value,
            address: event.target.deviceAddress.value,
            port: parseInt(event.target.devicePort.value, 10)
        });

        event.preventDefault();
    };

    render() {
        const {group} = this.props;

        return (
            <form onSubmit={this.handleSubmit}>
                <div className="form-group">
                    <label htmlFor="groupName">Group Name</label>
                    <input type="text"
                           className="form-control"
                           id="groupName"
                           name="groupName"
                           placeholder="Group Name"
                           required
                        //    defaultValue={group.name}
                           />
                </div>

                <div className="form-group">
                    <label htmlFor="selectDevice">Add device to group</label>
                    <select className="form-control" 
                            id="selectDevice" 
                            name="selectDevice" 
                            placeholder="Select device to group">
                        <option>1</option>
                        <option>2</option>
                        <option>3</option>
                        <option>4</option>
                        <option>5</option>
                    </select>
                </div>

                <div className="float-right">
                    <button type="submit" className="btn btn-primary mr-2">Submit</button>
                    <button type="button" className="btn btn-default" onClick={this.handleCancelClick}>Cancel</button>
                </div>
            </form>
        );
    }
}

GroupForm.defaultProps = {
    device: {
        name: '',
        address: '',
        port: 80
    }
};

GroupForm.propTypes = {
    device: devicePropType,
    onSubmit: PropTypes.func.isRequired
};
